/* eslint-disable no-undef */
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountryId,deleteActivity } from "../actions/index";
import "./Detail.css";
import NavBar from "./NavBar";

function Detail(props) {
  const [loading, setLoading] = useState(true);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getCountryId(props.match.params.id));
  }, []);

  const country = useSelector((state) => state.Detail);

  if (loading === true) {
    setTimeout(() => {
      setLoading(false);
    }, 500);

    // creamos el buttton de borrar el pais por id
    const handleDelete = (id) => {
      dispatch(deleteActivity(id));
      alert("Seguro que quieres borrar el pais")
    };




    return (
      <div class="loading show">
        <div class="spin"></div>
      </div>
    );
  } else if (loading === false) {
    return (
      <React.Fragment>

        <NavBar></NavBar>

        <div className="country-detail">
          {country ? (
            <div className="country-info">
              <img className="img-detail" src={country.flag} alt="country" />

              <div className="description-detail">
                <h1>{country.name}</h1>
                <h3>ID : {country.id}</h3>
                <h3>Capital : {country.capital}</h3>
                <h3>Poblacion : {country.population}</h3>
                <h3>Area : {country.area}</h3>
                <h3>SubRegion : {country.subregion}</h3>
              </div>

            </div>
            ) : (
            <p> Cargando... Espere un momento</p>

          )}
        </div>
           <div>
          {country.activities ? (
            country.activities.map((item) => (
              <div className="detail-activities">
                <h2>Actividad</h2>
                <h4>Nombre: {item.name}</h4>
                <h4>Dificultad: {item.difficulty}</h4>
                <h4>Duracion: {item.duration}</h4>
                <h4>Season :{item.season}</h4>
              </div>

            ))
          ) : (
            <p>'Todavia No Hay Actividades Por Mostrar'</p>
          )}
        </div>

      </React.Fragment>
    );
  }
}

export default Detail;
