import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import {getCountryByName} from '../actions/index'
import './SearchBar.css'
import {NavLink} from 'react-router-dom'


function SearchBar(props) {


  const dispatch = useDispatch()
  const [name, setName] = useState('')

  const handleInput = (e)=>{
    setName(e.target.value)
  }

  const handleSubmit = (e) =>{
    e.preventDefault()
    dispatch(getCountryByName(name))
    setName('')
    props.page(1)
  }
  return (
    <div className='container'>
      <NavLink className='navlink-home' to={'/'}>Atras</NavLink>
      <input className='search' value={name} type={'text'}  pattern="[a-zA-Z ]{2,}" placeholder='Buscar Pais'  onChange={(e)=> handleInput(e)} />

      <button disabled={name.length < 2 ? true : false} className='button' type='submit' onClick={(e) => handleSubmit(e)}>Buscar</button>
      <NavLink className='activity' to={'/createActivity'}>ACTIVIDAD</NavLink>
    </div>
  )
}

export default SearchBar
